refentry.refsect1:
&reftitle.parameters;




   filename



     調べたいファイル名。

  &reftitle.examples;



    is_uploaded_file の例


<?php

if (is_uploaded_file($_FILES['userfile']['tmp_name'])) {
   echo "ファイル ". $_FILES['userfile']['name'] ." のアップロードに成功しました。\n";
   echo "その中身を表示します\n";
   readfile($_FILES['userfile']['tmp_name']);
} else {
   echo "おそらく何らかの攻撃を受けました。";
   echo "ファイル名 '". $_FILES['userfile']['tmp_name'] . "'.";
}

?>
